import * as React from 'react';
import { request, setAuthHeader } from '../helpers/axios_helper';
export default class AuthContent extends React.Component {

 constructor(props) {
 super(props);
 this.state = {data:[]}
 };

 componentDidMount() {
 request(
 "GET",
 "/messages",
 {}).then(
  (response) => {
    const data = response.data.filter((item) => item.idclient === 'www')
    .map((item) => {
    return {
      id: item.id,
      type: item.type,
      timestart: new Date(item.timestart).toLocaleString(),
      timeend: new Date(item.timeend).toLocaleString(),
      price: item.price,
      status: item.status,
      idclient: item.idclient,
      idworker: item.idworker,
      idkart: item.idkart,
      idequipment: item.idequipment,
      idcenter: item.idcenter
    };
});
 this.setState({data: data})
 }).catch(
 (error) => {
 if (error.response.status === 401) {
 setAuthHeader(null);
 } else {
 this.setState({data: error.response.code})
 }

 }
 );
 };

 render() {
    return (
        <table className="table table-striped table-bordered">
        <thead className="table-dark">
          <tr>
            <th>Номер</th>
            <th>Тип</th>
            <th>Начало</th>
            <th>Конец</th>
            <th>Цена</th>
            <th>Статус</th>
            <th>Клиент</th>
            <th>Центр</th>
          </tr>
        </thead>
        <tbody>
          {this.state.data && this.state.data.map((item) => (
              <tr>
              <td>{item.id}</td>
              <td>{item.type}</td>
              <td>{item.timestart}</td>
              <td>{item.timeend}</td>
              <td>{item.price}</td>
              <td>{item.status}</td>
              <td>{item.idclient}</td>
              <td>{item.idcenter}</td>
              </tr>
          ))}
        </tbody>
      </table> 
    );
  };
}
