import React, { useState, useEffect } from 'react';
import { deleteApplicationById, getAllApplications } from '../services/ApplicationService';

function useApplications() {
  const [applications, setApplications] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchApplications() {
      try {
        const data = await getAllApplications();
        setApplications(data);
      } catch (error) {
        setError(error);
      }
    }

    fetchApplications();
  }, []);

  if (error) {
    console.error('Произошла ошибка при загрузке клиентов:', error);
    return null;
  }

  return applications;
}
function Delete(id, setApplications) {
  return async () => {
    await deleteApplicationById(id);
    setApplications((prevApplications) => prevApplications.filter((application) => application.id !== id));
  };
}

function ApplicationTable() {
  const applications = useApplications();
  const [applicationList, setApplicationList] = useState([]);

  useEffect(() => {
    setApplicationList(applications);
  }, [applications]);
  
  if (!applicationList) {
    return <div>Loading...</div>;
  }



  return (
    <table class="table table-striped table-bordered">
      <thead class="table-dark">
        <tr>
          <th>Номер</th>
          <th>Тип</th>
          <th>Начало</th>
          <th>Конец</th>
          <th>Цена</th>
          <th>Статус</th>
          <th>Клиент</th>
          <th>Рабочий</th>
          <th>Карт</th>
          <th>Экипировка</th>
          <th>Центр</th>
        </tr>
      </thead>
      <tbody>
        {applicationList.map((application) => (
          <tr key={application.id}>
            <td>{application.id}</td>
            <td>{application.type}</td>
            <td>{application.timestart}</td>
            <td>{application.timeend}</td>
            <td>{application.price}</td>
            <td>{application.status}</td>
            <td>{application.client.id}</td>
            <td>{application.worker.id + " " +application.worker.name}</td>
            <td>{application.kart.id}</td>
            <td>{application.equipment.id}</td>
            <td>{application.kartingcenter.id}</td>
            <button onClick={Delete(application.id, setApplicationList)} class="btn btn-danger" >Удалить</button>
            <button>Добавить</button>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default ApplicationTable;
