export async function getAllApplications() {
    const response = await fetch('http://localhost:8080/applications');
    const data = await response.json();
    return data;
  }
  
  export async function deleteApplicationById(id) {
    await fetch(`http://localhost:8080/applications/${id}`, {
      method: 'DELETE'
    });
  }
  
  export async function saveApplication(application) {
    await fetch('http://localhost:8080/applications', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(application)
    });
  }
  
  export async function getApplicationById(id) {
    const response = await fetch(`http://localhost:8080/applications/${id}`);
    const data = await response.json();
    return data;
  }
  
  export async function editApplication(application) {
    await fetch('http://localhost:8080/applications', {
